var m = flow.get('graphData')|| {"data":[],"series":[],"labels":[]};
flow.mode = flow.get('mode') || 'CV';

var cycle, ms, mV, uA;


switch (msg.topic){
    //when get msg from dropdown, choose graph type
    case 'graphType':
        flow.mode = msg.payload;
        break;

    //when get msg elsewhere, treat as data and format according graph type
    default:
        switch(flow.mode){
            case 'CV':
            case 'LSV':
                cycle = msg.payload.col1;
                mV = msg.payload.col2;
                uA = msg.payload.col3;
                //add new series and data array for each new cycle
                if(m.series.indexOf(cycle)==-1){
                    m.series.push(cycle);
                    m.data.push([]);
                }
                m.data[m.series.indexOf(cycle)].push({"x":mV,"y":uA});
                break;
            case 'CA':
                if(m.series.indexOf('CA')==-1){
                    m.series.push('CA');
                    m.data.push([]);
                }
                ms = msg.payload.col1;
                uA = msg.payload.col3;
                m.data[m.series.indexOf('CA')].push({"x":ms,"y":uA});
                break;

        }

}



// store the value back
flow.set('graphData',m);
// make it part of the outgoing msg object
msg = {};
msg.payload = [m];
return msg;

